import React from "react";
import "./App.css";
import Header from "./components/Header";
import Home from "./components/Home";
import Detail from "./components/Detail";
import { BrowserRouter as Router, Switch, Route } from 'react-router-dom';

function App() {
  return (
    <div className="App">
      <Router>
        <Header />
        <Switch>
          <Routes path="/detail"  >
            <Detail />  
          </Routes>  
          <Routes path="/">
            <Home  />
          </Routes   >
        </Switch>     
      </Router>
    </div>
  );
}

export default App;
