import React from 'react';
import styled from 'styled-components';

function Detail() {
  return (
    <Container>
      <Background>
         <img src='/images/Disney.jpg'/>
      </Background>
      <ImageTitle>
          <img src='/images/viewers-disney.png'/>
      </ImageTitle>
      <Controls>
        <PlayButton>
          <img src='/images/play-icon-black.png'/>
          <span>PLAY</span>
        </PlayButton>
        <TrailerButton>
          <img src='/images/play-icon-white.png'/>
          <span>Trailer</span>
        </TrailerButton>
        <AddButton>
          <span>+</span>
        </AddButton>
        <GroupWatchButton>
          <img src='/images/group-icon.png' />
        </GroupWatchButton>
      </Controls>
      <SubTitle>
        2018 * 7m * Family, Fantasy, Kids, Animation
      </SubTitle>
      <Description>
        Judul Film: "Harmony in the Enchanted Forest" Deskripsi: Dalam film Disney yang magis ini, kita dibawa ke dalam dunia yang penuh keajaiban yang disebut Hutan Ajaib. Di tengah-tengah hutan ini, hiduplah karakter utama kita, Luna, seorang anak perempuan muda yang memiliki kekuatan khusus untuk berkomunikasi dengan makhluk-makhluk ajaib.
      </Description>
    </Container>
  )
}

export default Detail

const Container = styled.div`
    min-height: calc(100vh - 70px);
    padding: 0 calc(3.5vw + 5px);
    position: relative;
`

const Background = styled.div`
    position: fixed;
    top: 0;
    left: 0;
    bottom: 0;
    right: 0;
    z-index: -1;
    opacity: 0.8;

    img{
        width: 100%;
        height: 100%;
        object-fit: cover;
    }
`
const ImageTitle = styled.div`
    height : 30vh;
    min-height: 170px;
    width: 35vw;
    min-width: 200px;

    img {
        width: 100%;
        height: 100%;
        object-fit: contain;
    }
`

const Controls = styled.div`
    display: flex;
    align-items: center;
`

const PlayButton = styled.button`
    border-radius: 4px;
    font-size: 15px;
    padding: 0 24px;
    margin-right: 22PX;
    display: flex;
    align-items: center;
    height: 56px;
    background: rgb(249, 249, 249);
    border: none;
    letter-spacing: 1.8px;
    cursor: pointer;

    &:hover {
      background: rgb(198, 198, 198);
    }
`

const TrailerButton = styled(PlayButton)`
    background: rgba(0,0,0, 0.3);
    border: 1px solid rgb(249, 249, 249);
    color : rgb(249, 249, 249);
    text-transform: uppercase;

`

const AddButton = styled.button`
    margin-right: 16px;
    width: 44px;
    height: 44px;
    display: flex;
    align-items: center;
    justify-content: center;
    border-radius: 50%;
    border: 2px solid white;
    background-color: rgba(0, 0, 0, 0.6);
    cursor: pointer;

    span{
      font-size: 30px;
      color: white;
    }
`

